def knight(p1, p2):
    # Map generated by knightmap.py
    knightMap= [[7, 6, 5, 6, 5, 6, 5, 6, 5, 6, 5, 6, 5, 6, 7],
                [6, 5, 6, 5, 4, 5, 4, 5, 4, 5, 4, 5, 6, 5, 6],
                [5, 6, 5, 4, 5, 4, 5, 4, 5, 4, 5, 4, 5, 6, 5],
                [6, 5, 4, 5, 4, 3, 4, 3, 4, 3, 4, 5, 4, 5, 6],
                [5, 4, 5, 4, 3, 4, 3, 4, 3, 4, 3, 4, 5, 4, 5],
                [6, 5, 4, 3, 4, 5, 2, 3, 2, 5, 4, 3, 4, 5, 6],
                [5, 4, 5, 4, 3, 2, 3, 4, 3, 2, 3, 4, 5, 4, 5],
                [6, 5, 4, 3, 4, 3, 4, 1, 4, 3, 4, 3, 4, 5, 6],
                [5, 4, 5, 4, 3, 2, 3, 4, 3, 2, 3, 4, 5, 4, 5],
                [6, 5, 4, 3, 4, 5, 2, 3, 2, 5, 4, 3, 4, 5, 6],
                [5, 4, 5, 4, 3, 4, 3, 4, 3, 4, 3, 4, 5, 4, 5],
                [6, 5, 4, 5, 4, 3, 4, 3, 4, 3, 4, 5, 4, 5, 6],
                [5, 6, 5, 4, 5, 4, 5, 4, 5, 4, 5, 4, 5, 6, 5],
                [6, 5, 6, 5, 4, 5, 4, 5, 4, 5, 4, 5, 6, 5, 6],
                [7, 6, 5, 6, 5, 6, 5, 6, 5, 6, 5, 6, 5, 6, 7]]
    charMap = {"a":0, "b":1, "c":2, "d":3, "e":4, "f":5, "g":6, "h":7}
    
    x, y = charMap[p1[0]], int(p1[1]) - 1
    tx, ty = charMap[p2[0]], int(p2[1]) - 1
    dx, dy = tx - x, ty - y
    
    return knightMap[7 + dy][7 + dx] - 1